homeApp.controller("authorsController",["$scope","authorService","sharedService",function(a,b,c){a.get_authors_interviewed=function(){if(!a.info.loading){a.info.loading=!0,angular.isUndefined(a.authors_interviewed)&&(a.authors_interviewed=[]);var c=a.authors_interviewed.length;b.get_authors_interviewed(c).then(function(b){a.info.loading=!1,a.authors_interviewed=a.authors_interviewed.concat(b)})}},a.handle_scroll_bottom=function(){a.get_authors_interviewed()},a.render_page=function(a){c.render_page(a)};(function(){a.get_authors_interviewed()})()}]);